import React, { useState, useRef, useEffect, useCallback } from "react";
import "./Viewport.scss";
import "./useAnimationFrame";

// private variable for this component
let currentZoom = 0;
let currentCameraPos = { x: 0, y: 0 };

// Canvas Implementation of the
function Viewport(props) {
  const canvasWidth = props.width;
  const canvasHeight = props.height;

  const canvasRef = useRef(null);
  const ctxRef = useRef(null);

  const [imgLoaded, setImgLoaded] = useState([false]);
  const [imgList, setImageList] = useState([]);

  useEffect(() => {
    // initialise the canvas

    // canvas update

    return () => {};
  }, [props.cameraPosition, props.zoom, imgLoaded, imgList]);

  useEffect(() => {
    ctxRef.current = canvasRef.current.getContext("2d");

    // Load the svg files
    let loadedImageCount = 0;
    setImageList(
      props.src.map((imgUrl) => {
        const img = new Image();
        img.src = imgUrl;
        img.addEventListener("load", () => {
          console.log("Viewport loaded image: " + imgUrl);
          loadedImageCount++;

          if (imgLoaded === imgList.length) {
            // all the image is loaded
            setImgLoaded(true);
          }
        });
        return img;
      })
    );

    requestAnimationFrame(update);
    return () => {};
  }, []); // load files only once, no dependency

  return <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight} />;
}

function repaintCanvas(ctx, imgs, cameraPosition, zoom) {
  renderImages(ctx, imgs);
  ctx.fillText("camera x - " + cameraPosition.x, 10, 50);
  ctx.fillText("camera y - " + cameraPosition.y, 10, 65);
}

function renderImages(ctx, imgs) {
  const paddingBetweenImages = 0;
  let currentDrawY = 0;
  for (let i = 0; i < imgs.length; i++) {
    // render all the images
    ctx.drawImage(imgs[i], 0, currentDrawY);

    currentDrawY += imgs[i].height + paddingBetweenImages;
  }
}

function clamp(value, min, max) {
  if (value < min) return min;
  if (value > max) return max;
  return value;
}

export default Viewport;

//resources

