import React, { useState, useRef, useEffect, useCallback } from "react";
import "./Viewport.scss";

// purpose of this component

// Referenced tutorial
// https://stackoverflow.com/questions/20926551/recommended-way-of-making-react-component-div-draggable

function Viewport(props) {
  // mode toggles for the components
  const [dragging, setDragging] = useState(false);

  const ZOOMMODE_IDLE = 0;
  const ZOOMMODE_IN = 1;
  const ZOOMMODE_OUT = 2;

  const [zooming, setZooming] = useState(ZOOMMODE_IDLE);
  // handling the position state
  const zoomStep = 10;
  const [viewportZoom, setViewportZoom] = useState(1);
  const [viewportOffsetPos, setViewportOffsetPos] = useState({ x: 0, y: 0 });
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  // grab the html reference of the container to get the beginning position
  const viewportContainerRef = useRef(null);

  function mouseClickHandler(e) {
    if (zooming === ZOOMMODE_IN) {
      setViewportZoom(viewportZoom + zoomStep);
    } else if (zooming === ZOOMMODE_OUT) {
    }
  }

  function mouseDownHandler(e) {
    // enter the moving state
    e.stopPropagation();
    e.preventDefault();

    // enter dragging mode
    setDragging(true);

    // initialise drag offset value
    const viewportElmPagePos = {
      x: viewportContainerRef.current.getBoundingClientRect().left,
      y: viewportContainerRef.current.getBoundingClientRect().top,
    };

    setDragOffset({
      x: e.nativeEvent.pageX - viewportElmPagePos.x - viewportOffsetPos.x,
      y: e.nativeEvent.pageY - viewportElmPagePos.y - viewportOffsetPos.y,
    });
  }

  function mouseUpHandler(e) {
    // exit dragging mode
    setDragging(false);
  }

  function mouseWheelHandler(e) {
    // stop the document from scrolling when the user mouse over this
    e.nativeEvent.preventDefault();

    // pan around the viewport using scroll wheel when the zooming mode is not active
    // setViewportOffsetPos({
    //   x: viewportOffsetPos.x - e.nativeEvent.deltaX,
    //   y: viewportOffsetPos.y - e.nativeEvent.deltaY,
    // });

    setViewportZoom(viewportZoom - e.nativeEvent.deltaY);
  }

  function mouseMoveHandler(e) {
    if (dragging) {
      // more about clientX/pageX/screenX
      // https://stackoverflow.com/questions/6073505/what-is-the-difference-between-screenx-y-clientx-y-and-pagex-y/9335517

      setViewportOffsetPos({
        // for some reason react glitches when we use offsetX
        x: e.nativeEvent.pageX - dragOffset.x,
        y: e.nativeEvent.pageY - dragOffset.y,
      });
    }
  }

  // https://stackoverflow.com/questions/5353254/javascript-onkeydown-event-fire-only-once
  const handleKeyDown = useCallback((e) => {
    // when "z key is down
    if (e.which === 90 && !zooming) {
      setZooming(ZOOMMODE_IN);
      console.log("zoom start");
    }
  });

  const handleKeyUp = useCallback((e) => {
    // when "z key is up
    if (e.which === 90 && zooming) {
      setZooming(ZOOMMODE_IDLE);
    }

    console.log("zoom off");
  });

  const contentPosition = projectPoint(
    {
      x: viewportOffsetPos.x,
      y: viewportOffsetPos.y,
      z: viewportZoom,
    },
    500,
    {
      x: 0,
      y: 0,
    }
  );

  return (
    <div
      className="viewport-container"
      onMouseDown={mouseDownHandler}
      onMouseUp={mouseUpHandler}
      onMouseMove={mouseMoveHandler}
      onClick={mouseClickHandler}
      onWheel={mouseWheelHandler}
      ref={viewportContainerRef}
    >
      <div
        className="viewport-content"
        style={{
          perspectiveOrigin: `0,0`,
          transform: `perspective(500px) translate3d(${viewportOffsetPos.x}px, ${viewportOffsetPos.y}px, ${viewportZoom}px)`,
        }}
      >
        {props.src.map((imgUrl, index) => {
          return <img key={index} src={imgUrl} alt="Design Section" />;
        })}
      </div>
    </div>
  );
}

function projectPoint(point3d, perspective, trasnformOrigin) {
  // The scaleProjected will store the scale of the element based on its distance from the 'camera'
  const scaleProjected = perspective / (perspective + point3d.z);

  const resultPoint = {
    // The xProjected is the x position on the 2D world
    x: point3d.x * scaleProjected + trasnformOrigin.x,
    // The yProjected is the y position on the 2D world
    y: point3d.y * scaleProjected + trasnformOrigin.y,
  };

  return resultPoint;
}

export default Viewport;
