import React, { useState, useRef } from "react";
import "./Viewport.scss";

// purpose of this component

// Referenced tutorial
// https://stackoverflow.com/questions/20926551/recommended-way-of-making-react-component-div-draggable

function Viewport(props) {
  const [dragging, setDragging] = useState(false);
  const [viewportOffsetPos, setViewportOffsetPos] = useState({ x: 0, y: 0 });
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  // grab the html reference of the container to get the beginning position
  const viewportContentRef = useRef(null);

  function mouseDownHandler(e) {
    // enter the moving state
    e.stopPropagation();
    e.preventDefault();

    // enter dragging mode
    setDragging(true);

    // initialise drag offset value
    const viewportContentRef = {
      x: viewportContainerRef.current.getBoundingClientRect().left,
      y: viewportContainerRef.current.getBoundingClientRect().top,
    };

    console.log(viewportElmPagePos);

    setDragOffset({
      x: e.nativeEvent.pageX - viewportElmPagePos.x,
      y: e.nativeEvent.pageY - viewportElmPagePos.y,
    });
  }

  function mouseUpHandler(e) {
    // exit dragging mode
    setDragging(false);
  }

  function mouseMoveHandler(e) {
    if (dragging) {
      // more about clientX/pageX/screenX
      // https://stackoverflow.com/questions/6073505/what-is-the-difference-between-screenx-y-clientx-y-and-pagex-y/9335517

      setViewportOffsetPos({
        // for some reason react glitches when we use offsetX
        x: e.nativeEvent.pageX - dragOffset.x,
        y: e.nativeEvent.pageY - dragOffset.y,
      });
    }
  }

  return (
    <div
      className="viewport-container"
      onMouseDown={mouseDownHandler}
      onMouseUp={mouseUpHandler}
      onMouseMove={mouseMoveHandler}
      ref={viewportContainerRef}
    >
      <div
        className="viewport-content"
        style={{
          transform: `translate(${viewportOffsetPos.x}px, ${viewportOffsetPos.y}px)`,
        }}
      >
        {props.src.map((imgUrl, index) => {
          return <img key={index} src={imgUrl} alt="Design Section" />;
        })}
      </div>
    </div>
  );
}

export default Viewport;
