import React, { useState, useRef, useEffect, useCallback } from "react";
import "./Viewport.scss";

// Canvas Implementation of the
function Viewport(props) {
  const canvasWidth = 1920;
  const canvasHeight = 1080;

  const canvasRef = useRef(null);

  const [imgLoaded, setImageLoaded] = useState(false);

  useEffect(() => {
    // initialise the canvas
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // clear everything on the canvas
    ctx.clearRect(0, 0, window.innerHeight, window.innerWidth);

    // ctx.fillStyle = "#FF0000";
    ctx.fillRect(0, 0, window.innerWidth, window.innerHeight);

    ctx.translate(10, 10);

    // render svg file on canvas

    let loadedImage = 0;
    const imgList = props.src.map((imgUrl) => {
      const img = new Image();
      img.src = imgUrl;
      img.addEventListener("load", () => {
        console.log("Viewport loaded image: " + imgUrl);
        loadedImage++;

        if (loadedImage === imgList.length) {
          // all the image is loaded
          this.setImageLoaded(true);
        }
      });
    });

    const update = () => {
      repaintCanvas();
      requestAnimationFrame(update);
    };

    update();
  });

  return <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight} />;
}

function repaintCanvas(ctx, imgs, cameraPosition, zoom) {}

function clamp(value, min, max) {
  if (value < min) return min;
  if (value > max) return max;
  return value;
}

export default Viewport;

