import React from "react";
import { useState, useRef } from "react";
import Viewport from "./viewport";

// utility function for min-maxing
function clamp(value, min, max) {
  if (value < min) return min;
  if (value > max) return max;
  return value;
}

function DesignExplorer() {
  const imgList = [
    `${process.env.PUBLIC_URL}/img/8 Viewing your tasks.svg`,
    `${process.env.PUBLIC_URL}/img/9 Contacting a client.svg`,
    `${process.env.PUBLIC_URL}/img/10 Contacting a helper.svg`,
  ];

  // static parameters
  const ZOOM_STEP = 10;
  const MAX_ZOOM = 100;
  const MIN_ZOOM = -400;

  // for controlling the viewports
  const [dragging, setDragging] = useState(false);
  const [viewportZoom, setViewportZoom] = useState(1);
  const [viewportCameraPos, setViewportCameraPos] = useState({ x: 0, y: 0 });
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  // refs
  const viewportContainerRef = useRef(null);

  function mouseDownHandler(e) {
    // enter the moving state
    e.stopPropagation();
    e.preventDefault();

    // initialize the dragging parameter
    const viewportElmPagePos = {
      x: viewportContainerRef.current.getBoundingClientRect().left,
      y: viewportContainerRef.current.getBoundingClientRect().top,
    };

    setDragOffset({
      x: e.nativeEvent.pageX - viewportElmPagePos.x - viewporCameraPos.x,
      y: e.nativeEvent.pageY - viewportElmPagePos.y - viewporCameraPos.y,
    });

    // enter dragging mode
    setDragging(true);
  }

  function mouseUpHandler(e) {
    // exit dragging mode
    setDragging(false);
  }

  function mouseWheelHandler(e) {
    // stop the document from scrolling when the user mouse over this
    e.nativeEvent.preventDefault();

    // pan around the viewport using scroll wheel when the zooming mode is not active
    // setViewportOffsetPos({
    //   x: viewportOffsetPos.x - e.nativeEvent.deltaX,
    //   y: viewportOffsetPos.y - e.nativeEvent.deltaY,
    // });

    // calculate zoom value
    const newZoomValue = viewportZoom - e.nativeEvent.deltaY;
    // set the zoom level
    setViewportZoom(clamp(newZoomValue, MIN_ZOOM, MAX_ZOOM));
  }

  function mouseMoveHandler(e) {
    if (dragging) {
      const maxPos = {
        x: 0,
        y: 0,
      };
      const minPos = {
        x: -(
          viewportContainerRef.current.scrollWidth -
          viewportContainerRef.current.clientWidth
        ),
        y: -(
          viewportContainerRef.current.scrollHeight -
          viewportContainerRef.current.clientHeight
        ),
      };

      setViewportCameraPos({
        // for some reason react glitches when we use offsetX
        x: clamp(e.nativeEvent.pageX - dragOffset.x, minPos.x, maxPos.x),
        y: clamp(e.nativeEvent.pageY - dragOffset.y, minPos.y, maxPos.y),
      });
    }
  }
  return (
    <div ref={viewportContainerRef} className="viewport-container">
      <Viewport
        onMouseDown={mouseDownHandler}
        onMouseUp={mouseUpHandler}
        onMouseMove={mouseMoveHandler}
        camearPosition={viewportCameraPos}
        zoom={viewportZoom}
        src={imgList}
      />
    </div>
  );
}

export default DesignExplorer;
